    //fungsi mengambil data
    public static ArrayList<Batik> getListData(){
        Batik batik = null;

        ArrayList<Batik> list = new ArrayList<>();
        for (int i = 0; i < data.length; i++){
            batik = new Batik();
            batik.setNama(data[i][0]);
            batik.setCiri(data[i][1]);
            batik.setDeskripsi(data[i][2]);
            batik.setFoto(foto[i]);

            list.add(batik);
        }
        return list;
    }

